import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  submitted: boolean = false;
  loginForm: FormGroup;

  constructor(
    private router: Router,
    private fb: FormBuilder
  ) {
      this.loginForm = this.fb.group({
        email: ['', [Validators.required, Validators.email]],
        password: ['', [Validators.required]]
      });
  }

  ngOnInit(): void {

  }

  toSignup() {
    this.router.navigate(['/signup'])
  }

  loginSubmit() {
    this.submitted = true;
    console.log('Login value', this.loginForm);
  };

}
<div class="container">
  <div id="login-box">
    <div class="left">
      <h1 class="text-secondary">Login</h1>

      <form [formGroup]="loginForm" (ngSubmit)="loginSubmit()">
        <input type="text" name="email" class="form-control" formControlName="email" placeholder="E-mail" />
        <div class="text-danger" *ngIf="submitted || loginForm.controls['email']?.invalid && loginForm.controls['email']?.touched">
          <div *ngIf="loginForm.controls['email']?.errors?.['required']">
            Email is required
          </div>
          <div *ngIf="loginForm.controls['email']?.errors?.['email']">
            Enter valid email
          </div>
        </div>

        <input type="password" name="password" class="form-control" formControlName="password" placeholder="Password" />
        <div class="text-danger" *ngIf="submitted || loginForm.controls['password']?.invalid && loginForm.controls['password']?.touched">
          <div *ngIf="loginForm.controls['password'].errors?.['required']">
            Password is required
          </div>
        </div>

        <input type="submit" name="signup_submit" value="Login" />&nbsp;
        <span (click)="toSignup()">
          Not have account? Create account
        </span>
      </form>
    </div>

    <div class="right">
      <span class="loginwith text-secondary">Sign in with<br />social network</span>

      <button class="social-signin facebook">Log in with facebook</button>
      <button class="social-signin twitter">Log in with Twitter</button>
      <button class="social-signin google">Log in with Google+</button>
    </div>
  </div>
</div>
