import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { TeacherService } from 'src/app/services/teacher.service';
import { Teacher } from 'src/model/teacher.model';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.scss']
})
export class CreateComponent implements OnInit {
  teacher: Teacher;
  createForm: FormGroup;
  submitted: boolean = false;

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private route: ActivatedRoute,
    private teacherService: TeacherService
  ) {
      this.createForm = this.fb.group({
        name: ['', [Validators.required]],
        experience: ['', [Validators.required]],
        previousEmployer: ['', [Validators.required]]
      })
      this.teacher = new Teacher();
  }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      let id = params['id'];
      if(id !== undefined) {
        this.teacherService.getTeacherid(id).subscribe(res => {
          this.teacher = res;
          this.createForm.patchValue(this.teacher);
        })
      }
    })
  }


  subCreate() {
    this.submitted = true;

    if (this.createForm.valid) {
      this.teacherService.createTeacher(this.createForm.value).subscribe(
        () => {
          this.router.navigate(['/dashboard']);
        }
      )
    }
  }

}
<div class="container">
  <form [formGroup]="createForm" (ngSubmit)="subCreate()">
    <div class="mb-3">
      <label for="" class="form-label">Name</label>
      <input type="text" name="" id="" class="form-control" formControlName="name" placeholder="" aria-describedby="helpId">
    </div>
    <div class="mb-3">
      <label for="" class="form-label">Experience</label>
      <input type="text" name="" id="" class="form-control"formControlName="experience" placeholder="" aria-describedby="helpId">
    </div>
    <div class="mb-3">
      <label for="" class="form-label">Previous employer</label>
      <input type="text" name="" id="" class="form-control" formControlName="previousEmployer" placeholder="" aria-describedby="helpId">
    </div>

    <button class="btn btn-primary" type="submit">Create</button>
  </form>
</div>
